Node.Inputs = {
    ObnizID: 'string',
}

Node.Outputs = {
    temperature: "number",
    humidity: "number",
    power:"number",
    rssi: "number",
    name: "string",
}


//let obniz;

Node.Signals.ScriptLoaded = function () {
    var obniz = new Obniz(Node.Inputs.ObnizID);
    obniz.onconnect = async function () {
        await obniz.ble.initWait();
        var target = {
            //localName: "GVH5075_XXXX",
            localNamePrefix:"GVH5075"
        };

        obniz.ble.scan.onfind = async function (peripheral) {
            Node.Outputs.name=peripheral.localName;
            console.log("Name:"+peripheral.localName);

            const buf = peripheral.adv_data[26] * Math.pow(16, 4) + peripheral.adv_data[27] * Math.pow(16, 2) + peripheral.adv_data[28]
            Node.Outputs.temperature = Math.floor(buf / 1000) / 10;
            Node.Outputs.humidity = (buf % 1000) / 10;
            Node.Outputs.power=peripheral.adv_data[29];
            Node.Outputs.rssi=peripheral.rssi;
            
            //console.log(buf);
            //console.log(temperature);
            //console.log(pressure); 
            //console.log("RSSI:"+peripheral.rssi);

        };

        obniz.ble.scan.onfinish = async function (peripherals, error) {
            console.log("scan again")
            await obniz.ble.scan.startWait(target);
        };

        await obniz.ble.scan.startWait(target);
    };
}
